import { ActionReducerMap, createFeatureSelector, MetaReducer } from '@ngrx/store';
import { environment as env } from '@env/environment';
import { debugReducer, initStateFromLocalStorage } from '@app/core/meta-reducers/init-state-storage.reducer';
import { routerReducer, RouterReducerState, SerializedRouterStateSnapshot } from '@ngrx/router-store';
import { languageReducer } from './language/store';
import { LanguageType } from './language/language.models';

export interface AppState {
  language: LanguageType;
  router: RouterReducerState<SerializedRouterStateSnapshot>;
}

export const reducers: ActionReducerMap<AppState> = {
  language: languageReducer,
  router: routerReducer,
};

export const metaReducers: MetaReducer<AppState>[] = [initStateFromLocalStorage];

if (!env.production) metaReducers.unshift(debugReducer);

export const selectLanguageState = createFeatureSelector<LanguageType>('language');

export const selectRouterState = createFeatureSelector<RouterReducerState<SerializedRouterStateSnapshot>>('router');

export const APP_PREFIX = 'test-APP';

export interface IButton {
  onAction?: () => void;
  id?: string;
  name?: string;
  className?: string;
  customClass?: string;
  tooltipTitle?: string;
  tooltipClassName?: string;
  isRound?: boolean;
  icon?: string;
  [name: string]: string | number | boolean | any;
  disabled?: boolean;
}

export interface ICoord {
  lon?: number;
  lat?: number;
}

export interface IWeather {
  id?: number;
  main?: string;
  description?: string;
  icon?: string;
}

export interface IWeatherMain {
  temp?: number;
  feels_like?: number;
  temp_min?: number;
  temp_max?: number;
  pressure?: number;
  humidity?: number;
}

export interface IWeatherWind {
  speed?: number;
  deg?: number;
}

export interface IWeatherSys {
  type?: number;
  id?: number;
  country?: string;
  sunrise?: number;
  sunset?: number;
}

export interface IWeatherCity {
  id?: number;
  name?: string;
  country?: string;
  coord?: ICoord;
}

export interface IWeatherClouds {
  all?: number;
}

export interface IForecast {
  dt?: number;
  main?: IWeatherMain;
  weather?: IWeather[];
  clouds?: IWeatherClouds;
  wind?: IWeatherWind;
  visibility?: number;
  pop?: number;
  dt_txt?: string;
}

export interface IWeatherData {
  coord?: ICoord;
  weather?: IWeather[];
  base?: string;
  main?: IWeatherMain;
  visibility?: number;
  wind?: IWeatherWind;
  clouds?: IWeatherClouds;
  dt?: number;
  sys?: IWeatherSys;
  timezone?: number;
  id?: number;
  name?: string;
  cod?: number;
  city?: IWeatherCity;
  sunrise?: string;
  sunset?: string;
  forecast?: IForecast[];
}

import { AfterViewInit, Component, ElementRef, Input, OnDestroy, ViewChild } from '@angular/core';
import { BehaviorSubject, of, Observable } from 'rxjs';
import { catchError, debounceTime, distinctUntilChanged, map, switchMap } from 'rxjs/operators';
import Map from 'ol/Map';
import Overlay from 'ol/Overlay';
import { toLonLat } from 'ol/proj';
import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import { Feature } from 'ol';
import { Point } from 'ol/geom';
import Style from 'ol/style/Style';
import Icon from 'ol/style/Icon';
import { UntilDestroy, untilDestroyed } from '@ngneat/until-destroy';
import { WeatherPopupService } from './maps-popup.service';

export interface IDataDisplay {
  label?: string;
  value?: string | number | null;
}

export interface IWeatherData {
  [name: string]: any;
}

@UntilDestroy()
@Component({
  selector: 'app-maps-popup',
  templateUrl: './maps-popup.component.html',
  styleUrl: './maps-popup.component.scss',
})
export class MapPopupComponent extends WeatherPopupService implements AfterViewInit, OnDestroy {
  @ViewChild('popup') popupEl: ElementRef;

  @Input() mapView: Map;

  private overlay: Overlay;

  private markerLayer: VectorLayer<VectorSource>;

  private currentMarker: Feature;

  private coordinatesSubject = new BehaviorSubject<number[]>([]);

  details$: Observable<IDataDisplay[]> = this.coordinatesSubject.pipe(
    debounceTime(300),
    distinctUntilChanged((prev, curr) => prev[0] === curr[0] && prev[1] === curr[1]),
    switchMap(([lon, lat]) =>
      this.getWeatherData(lat, lon).pipe(
        map(data => (data ? this.mapToDetails(data) : [])),
        catchError(() => of([{ label: 'Data unavailable', value: '' }]))
      )
    ),
    untilDestroyed(this)
  );

  ngAfterViewInit(): void {
    this.overlay = new Overlay({
      element: this.popupEl.nativeElement,
      offset: [0, -13],
    });
    this.markerLayer = new VectorLayer({
      source: new VectorSource(),
    });

    this.mapView.addOverlay(this.overlay);
    this.mapView.addLayer(this.markerLayer);
    this.setupPopup();
  }

  ngOnDestroy(): void {
    if (this.overlay) this.mapView.removeOverlay(this.overlay);
    if (this.markerLayer) this.mapView.removeLayer(this.markerLayer);
  }

  setupPopup(): void {
    this.mapView.on('singleclick', event => {
      const { coordinate } = event;
      const [lon, lat] = toLonLat(coordinate);
      this.coordinatesSubject.next([lon, lat]);
      this.addOrUpdateMarker(coordinate);
      this.overlay.setPosition(coordinate);
      const popupRect = this.popupEl.nativeElement.getBoundingClientRect();
      this.overlay.setOffset([-popupRect.width / 2, -popupRect.height - 30]);
    });
  }

  private mapToDetails(data: IWeatherData): IDataDisplay[] {
    const { wind, clouds, main } = data || {};
    return [
      { label: 'Temperature:', value: `${Math.floor((main?.temp as number) ?? 0)}Â°C` },
      { label: 'Wind:', value: `${Math.floor((wind?.speed as number) ?? 0)}m/s` },
      { label: 'Cloud cover:', value: `${(clouds?.all as number) ?? 0}%` },
      { label: 'Pressure:', value: `${(main?.pressure as number) ?? 0}hPa` },
    ];
  }

  private addOrUpdateMarker(coordinate: number[]): void {
    if (this.currentMarker) {
      const currentCoord = (this.currentMarker.getGeometry() as Point).getCoordinates();
      if (currentCoord[0] === coordinate[0] && currentCoord[1] === coordinate[1]) return;
      this.markerLayer.getSource().removeFeature(this.currentMarker);
    }

    const marker = new Feature({
      geometry: new Point(coordinate),
    });

    marker.setStyle(
      new Style({
        image: new Icon({
          anchor: [0.5, 1],
          src: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
          scale: 0.05,
        }),
      })
    );

    this.markerLayer.getSource().addFeature(marker);
    this.currentMarker = marker;
  }

  closePopup(): void {
    this.overlay.setPosition(undefined);
    if (this.currentMarker) {
      this.markerLayer.getSource().removeFeature(this.currentMarker);
      this.currentMarker = null;
    }
  }
}

